import telebot
from telebot import types
import config

bot = telebot.TeleBot(config.TOKEN)

user_responses = {}

@bot.message_handler(commands=['start'])
def Response(message):
    markup = types.ReplyKeyboardMarkup(row_width=2, resize_keyboard=True)
    button1 = types.KeyboardButton("Choose a game")
    button2 = types.KeyboardButton("Developers` choice")
    markup.row(button1, button2)
    bot.send_message(message.chat.id,
                     'Hey there! ðŸ‘‹ \n\nI`m your friendly Python-powered bot, \n and I can choose a game for u <3\n--------------------OR--------------------'
                     '\nYou can try "Developers` choice"'
                     '\n(This is list of games that developers advise to pass)'
                     '\n\nSo, what you will get)', reply_markup=markup)
    bot.register_next_step_handler(message, first_question)

